import { Injectable } from '@angular/core';
import { Categoria } from '../interfaces/categoria';
import { Observable } from 'rxjs';
import { RespostaApi } from '../interfaces/api-dto/respostaApi';
import { HttpClient } from '@angular/common/http';

@Injectable({
  providedIn: 'root'
})
export class CategoriaService {

  constructor(private http: HttpClient) { }

  private readonly API = "https://localhost:7012/api/Categroria";

  async listar(): RespostaApi<Categoria[]> {
    return this.http.get<RespostaApi<Categoria[]>>(this.API).pipe(
      (resposta) => resposta.toPromise());
  }

  buscar(busca: string): Observable<RespostaApi<Categoria[]>> {
    const url = `${this.API}/${busca}`
    return this.http.get<RespostaApi<Categoria[]>>(url)
  }
}
