import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { TipoContaEnum } from 'src/app/enums/tipoContaEnum';
import { CreateContaDto } from 'src/app/interfaces/api-dto/financas/createContaDto';
import { CreateContaVariavelDto } from 'src/app/interfaces/api-dto/financas/createContaVariavelDto';
import { ContaVariavelService } from 'src/app/services/Financas/conta-variavel.service';
import { ContaService } from 'src/app/services/Financas/conta.service';
import { CategoriaService } from 'src/app/services/categoria.service';
import { ValidacaoService } from 'src/app/services/validacao.service';

@Component({
  selector: 'app-incluir-conta',
  templateUrl: './incluir-conta.component.html',
  styleUrls: ['./incluir-conta.component.css']
})
export class IncluirContaComponent implements OnInit {

  Conta: CreateContaDto = { } as CreateContaDto;

  TipoContaId: number = 0;
  Categorias$ = this.serviceCategoria.listar();

  constructor(
    private serviceConta: ContaService,
    private serviceContaVariavel: ContaVariavelService,
    private serviceCategoria: CategoriaService,
    private serviceValidacao: ValidacaoService,
    private router: Router
    ) { }

  ngOnInit(): void {
    this.Conta.CategoriaId = 0;
  }

  selecionaTipoConta() {
    let tipoConta = (<HTMLInputElement>document.getElementById("selTipoConta")).value;
    if (tipoConta == TipoContaEnum.Fixa.toLocaleString()) {
      document.getElementById("divValor")!.style.display = "flex";
    } else {
      document.getElementById("divValor")!.style.display = "none";
    }
  }

  salvarConta() {
    if (this.TipoContaId == TipoContaEnum.Fixa) {
      this.serviceConta.incluir(this.Conta).subscribe(() => {
        this.router.navigate(['/contas']);
      });
    }
    else if (this.TipoContaId == TipoContaEnum.Variavel){
      let contaVariavel: CreateContaVariavelDto = {
        Descricao: this.Conta.Descricao,
        DiaVencimento: this.Conta.DiaVencimento,
        CategoriaId: this.Conta.CategoriaId
      };
      this.serviceContaVariavel.incluir(contaVariavel).subscribe(() => {
        this.router.navigate(['/contas']);
      });
    }
  }

  cancelar() {
    this.router.navigate(['/contas']);
  }

  apenasNumeros(event: any) {
    if (!this.serviceValidacao.apenasNumeros(event)) {
      event.preventDefault();
    }
  }

  diaMes(event: any) {
    if (!this.serviceValidacao.diaMes(event)) {
      event.preventDefault();
    }
  }
}
<div class="container main ff-verdana">
  <div class="titulo-pagina ff-verdana">
    <h1>Inlcuir Conta</h1>
  </div>
  <form (ngSubmit)="salvarConta()" class="form conteudo-pagina">
    <div class="row">
      <div class="col-4">
        <div class="form-group">
          <label for="descricao">Descrição</label>
          <input type="text" class="form-control" id="txtDescricao" placeholder="Descrição" name="Descricao" [(ngModel)]="Conta.Descricao">
        </div>
      </div>
      <div class="col-2">
        <div class="form-group">
          <label for="diaVencimento">Dia venc.:</label>
          <input type="number" class="form-control" id="txtDiaVencimento" placeholder="Dia vencimento" name="DiaVencimento"
          (keypress)="diaMes($event)" (paste)="diaMes($event)" [(ngModel)]="Conta.DiaVencimento">
        </div>
      </div>
      <div class="col-3">
        <div class="form-group">
          <label for="tipoCconta">Tipo</label>
          <select name="tipoConta" id="selTipoConta" class="form-control" (change)="selecionaTipoConta()" name="TipoContaId" [(ngModel)]="TipoContaId">
            <option value="0">Selecione...</option>
            <option value="1">Variável</option>
            <option value="2">Fixa</option>
          </select>
        </div>
      </div>
      <div class="col-3">
        <div class="form-group">
          <label for="CategoriaId">Categoria</label>
          <select *ngIf="Categorias$ | async as categorias" name="CategoriaId" id="selCategoriaId" class="form-control" [(ngModel)]="Conta.CategoriaId">
            <option value="0">Selecione...</option>
            <option *ngFor="let categoria of categorias.Valor" [value]="categoria.Id">{{ categoria.Descricao}}</option>
          </select>
        </div>
      </div>
    </div>
    <div class="row" id="divValor">
      <div class="col-3">
        <div class="form-group">
          <label for="descricao">Valor inteiro</label>
          <input type="text" class="form-control" id="txtValorInteiro" placeholder="Valor" name="ValorInteiro"
          (keypress)="apenasNumeros($event)" (paste)="apenasNumeros($event)" [(ngModel)]="Conta.ValorInteiro">
        </div>
      </div>
      <div class="col-3">
        <div class="form-group">
          <label for="descricao">Valor centavos</label>
          <input type="text" class="form-control" id="txtValorCentavos" placeholder="Valor Centavos" name="ValorCentavos"
          (keypress)="apenasNumeros($event)" (paste)="apenasNumeros($event)" [(ngModel)]="Conta.ValorCentavos">
        </div>
      </div>
    </div>
    <div class="row">
      <div class="col-12">
        <div class="form-group">
          <button type="submit" id="btnSumbit" class="btn">Salvar</button>
          <button type="button" id="btnCancelar" class="btn btn-red" (click)="cancelar()">Cancelar</button>
        </div>
      </div>
  </div>
</form>
