import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Observable, of} from 'rxjs';
import { CategoriaEnum } from 'src/app/enums/categoriaEnum';
import { CreateContaDto } from 'src/app/interfaces/api-dto/financas/createContaDto';
import { CreateContaVariavelDto } from 'src/app/interfaces/api-dto/financas/createContaVariavelDto';
import { RespostaApi } from 'src/app/interfaces/api-dto/respostaApi';
import { Categoria } from 'src/app/interfaces/categoria';
import { SelectListItem } from 'src/app/interfaces/selectListItem';
import { ContaVariavelService } from 'src/app/services/Financas/conta-variavel.service';
import { ContaService } from 'src/app/services/Financas/conta.service';
import { CategoriaService } from 'src/app/services/categoria.service';

@Component({
  selector: 'app-incluir-conta',
  templateUrl: './incluir-conta.component.html',
  styleUrls: ['./incluir-conta.component.css']
})
export class IncluirContaComponent implements OnInit {

  constructor(
    private serviceConta: ContaService,
    private serviceContaVariavel: ContaVariavelService,
    private serviceCategoria: CategoriaService,
    private router: Router
    ) { }

  Conta: CreateContaDto = {
    Descricao: '',
    ValorInteiro: 0,
    ValorCentavos: 0,
    DiaVencimento: 0,
    CategoriaId: 0
  }
  TipoContaId: number = 0;
  Categorias$: Observable<RespostaApi<Categoria[]>> = new Observable<RespostaApi<Categoria[]>>();

  ngOnInit(): void {
    this.Categorias$ = this.serviceCategoria.listar();
  }

  selecionaTipoConta() {
    let tipoConta = (<HTMLInputElement>document.getElementById("tipoConta")).value;
    if (tipoConta == '1') {
      document.getElementById("divValor")!.style.display = "flex";
    } else {
      document.getElementById("divValor")!.style.display = "none";
    }
  }

  salvarConta() {
    if (this.TipoContaId == 1) {
      this.serviceConta.incluir(this.Conta).subscribe(() => {
        this.router.navigate(['/contas']);
      }
      );
    }
    else if (this.TipoContaId == 2){
      let contaVariavel: CreateContaVariavelDto = {
        Descricao: this.Conta.Descricao,
        DiaVencimento: this.Conta.DiaVencimento,
        CategoriaId: this.Conta.CategoriaId
      };
      this.serviceContaVariavel.incluir(contaVariavel).subscribe(() => {
        this.router.navigate(['/contas']);
      }
      );
    }
  }

  cancelar() {
    this.router.navigate(['/contas']);
  }
}
<div class="container main ff-verdana">
  <div class="titulo-pagina ff-verdana">
    <h1>Inlcuir Conta</h1>
  </div>
  <div class="conteudo-pagina">
    <div class="row form-row">
      <div class="col-4">
        <div class="form-group">
          <label for="descricao">Descrição</label>
          <input type="text" class="form-control" id="descricao" placeholder="Descrição" [(ngModel)]="Conta.Descricao">
        </div>
      </div>
      <div class="col-2">
        <div class="form-group">
          <label for="diaVencimento">Dia venc.:</label>
          <input type="number" class="form-control" id="diaVencimento" placeholder="Dia vencimento" [(ngModel)]="Conta.DiaVencimento">
        </div>
      </div>
      <div class="col-3">
        <div class="form-group">
          <label for="tipoCconta">Tipo</label>
          <select name="tipoConta" id="tipoConta" class="form-control" (change)="selecionaTipoConta()" [(ngModel)]="TipoContaId">
            <option value="0">Selecione...</option>
            <option value="1">Fixa</option>
            <option value="2">Variável</option>
          </select>
        </div>
      </div>
      <div class="col-3">
        <div class="form-group">
          <label for="CategoriaId">Categoria</label>
          <select name="CategoriaId" id="CategoriaId" class="form-control" [ngModel]="Conta.CategoriaId">
            <option value="0">Selecione...</option>
            <option *ngFor="let categoria of (Categorias$ | async)!.Valor" [ngValue]="categoria.Id">{{ categoria.Descricao}}</option>
            <!-- <option value="1">Casa</option>
            <option value="2">Seguranca</option>
            <option value="3">Outros</option> -->
          </select>
        </div>
      </div>
    </div>
    <div class="row form-row" id="divValor">
      <div class="col-3">
        <div class="form-group">
          <label for="descricao">Valor inteiro</label>
          <input type="number" class="form-control" id="valorInteiro" placeholder="Valor" [(ngModel)]="Conta.ValorInteiro">
        </div>
      </div>
      <div class="col-3">
        <div class="form-group">
          <label for="descricao">Valor centavos</label>
          <input type="number" class="form-control" id="valorCentavos" placeholder="Valor Centavos" [(ngModel)]="Conta.ValorCentavos">
        </div>
      </div>
    </div>
    <div class="row form-row">
      <div class="col-12">
        <div class="form-group">
          <button type="button" class="btn" (click)="salvarConta()">Salvar</button>
          <button type="button" class="btn btn-red" (click)="cancelar()">Cancelar</button>
        </div>
      </div>
  </div>
</div>
