          <label for="tipoCconta">Tipo</label>
          <select name="tipoConta" id="tipoConta" class="form-control" (change)="selecionaTipoConta()" [(ngModel)]="TipoContaId">
            <option value="0">Selecione...</option>
            <option value="1">Variável</option>
            <option value="2">Fixa</option>
          </select>
        </div>
      </div>
      <div class="col-3">
        <div class="form-group">
          <label for="CategoriaId">Categoria</label>
          <select *ngIf="Categorias$ | async as categorias" name="CategoriaId" id="CategoriaId" class="form-control" [(ngModel)]="CategoriaId">
            <option value="0">Selecione...</option>
            <option *ngFor="let categoria of categorias.Valor" [value]="categoria.Id">{{ categoria.Descricao}}</option>
          </select>
        </div>
      </div>
    </div>
    <div class="row form-row" id="divValor">
      <div class="col-3">
        <div class="form-group">
          <label for="descricao">Valor inteiro</label>
          <input type="number" class="form-control" id="valorInteiro" placeholder="Valor" [(ngModel)]="Conta.ValorInteiro">
        </div>
      </div>
      <div class="col-3">
        <div class="form-group">
          <label for="descricao">Valor centavos</label>
          <input type="number" class="form-control" id="valorCentavos" placeholder="Valor Centavos" [(ngModel)]="Conta.ValorCentavos">
        </div>
      </div>
    </div>
    <div class="row form-row">
      <div class="col-12">
        <div class="form-group">
          <button type="button" class="btn" (click)="salvarConta()">Salvar</button>
          <button type="button" class="btn btn-red" (click)="cancelar()">Cancelar</button>
        </div>
      </div>
  </div>
</div>
