import { DadosModalExcluir } from './../../interfaces/dadosModalExcluir';
import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';

@Component({
  selector: 'app-modal-excluir',
  templateUrl: './modal-excluir.component.html',
  styleUrls: ['./modal-excluir.component.css']
})
export class ModalExcluirComponent implements OnInit {

  @Input() DadosModalExcluir: DadosModalExcluir = {} as DadosModalExcluir;
  @Output() ExcluirRegistro = new EventEmitter<DadosModalExcluir>();

  constructor() { }

  ngOnInit(): void {
  }

  excluirRegistro() {
    this.ExcluirRegistro.emit(this.DadosModalExcluir);
  }

  cancelar() {
    document.getElementById('modalExcluir')!.style.display = 'none';
  }

}
<section class="excluir-modal ff-verdana" id="modalExcluir">
  <div class="modal">
    <div class="cabecalho-modal">
      <span class="texto">Atenção</span><span class="img"><img src="../../../../assets/img/exclamacao-icon.png"></span>
    </div>
    <div class="conteudo-modal">
      <span>O registro <strong>"{{DadosModalExcluir.NomeRegistro}}"</strong> será excluido. <br /> Confirma a exclusão?</span>
    </div>
    <div class="acoes-modal">
      <button type="button" class="btn" id="btnCofirmar" title="Confirmar" (click)="excluirRegistro()">Excluir</button>
      &nbsp;
      <button type="button" class="btn btn-red" id="btnCancelar" title="Cancelar" (click)="cancelar()">Cancelar</button>
    </div>
  </div>
  <div class="overlay"></div>
</section>
