import { Component, OnInit } from '@angular/core';
import { TipoAlertaEnum } from 'src/app/enums/tipoAlertaEnum';
import { DadosFeedbackAlerta } from 'src/app/interfaces/dadosFeedbackAlerta';
import { Despesa } from 'src/app/interfaces/financas/Despesa';
import { DadosPaginador } from 'src/app/interfaces/paginacao/dadosPaginador';
import { DadosPaginados } from 'src/app/interfaces/paginacao/dadosPaginados';
import { ItemPagina } from 'src/app/interfaces/paginacao/itemPagina';
import { DespesaMapper } from 'src/app/mappers/financas/DespesaMapper';
import { DespesaService } from 'src/app/services/Financas/despesa.service';
import { FeedbackService } from 'src/app/services/feedback.service';
import { RespostaApiService } from 'src/app/services/resposta-api.service';

@Component({
  selector: 'app-painel-despesas',
  templateUrl: './painel-despesas.component.html',
  styleUrls: ['./painel-despesas.component.css']
})
export class PainelDespesasComponent implements OnInit {

  Ordem: string = '';
  NomeCampo: string = '';

  DadosPaginador: DadosPaginador = {
    PaginaAtual: 1,
    ItensPorPagina: 15,
    TotalItens: 0,
  };

  DadosPaginados: DadosPaginados<Despesa> = { } as DadosPaginados<Despesa>;
  DespesaAcao: Despesa = { } as Despesa;
  Despesas: Despesa[] = [];

  constructor(
    private despesaService: DespesaService,
    private feedbackService: FeedbackService,
    private respostaApiService: RespostaApiService,
    private despesaMapper: DespesaMapper
    ) {}

  ngOnInit(): void {
    this.despesaService.listar().subscribe({
      next: (respostaApi) => {
        respostaApi.valor.forEach(despesaDto => {
          this.Despesas.push(this.despesaMapper.DespesaDtoToDespesa(despesaDto));
        });
        this.ordenarDespesas();
      }, error: (err) => {
        let dadosFeedback = {
          Id: "feedback1",
          TipoAlerta: TipoAlertaEnum.Erro,
          Titulo: "Erro!",
          Mensagem: "Não foi possível obter as Despesas."
        } as DadosFeedbackAlerta;
        this.feedbackService.gerarFeedbackAlerta(dadosFeedback);
      }
    });
  }

  buscarDespesas(): void {
    let busca = (document.getElementById('txtBusca') as HTMLInputElement).value;
    this.Despesas = [];
    this.despesaService.buscar(busca).subscribe({
      next: (respostaApi) => {
        respostaApi.valor.forEach(despesaDto => {
          this.Despesas.push(this.despesaMapper.DespesaDtoToDespesa(despesaDto));
        });
        this.ordenarDespesas();
      }, error: (err) => {
        this.respostaApiService.tratarRespostaApi(err);
      }
    });
  }

  ordenarDespesas(nomeCampo?: string): void {
    if (nomeCampo == this.NomeCampo) {
      if (this.Ordem == 'desc') {
        this.Ordem = 'asc';
      } else if (this.Ordem == 'asc') {
        this.Ordem = '';
        this.NomeCampo = '';
        nomeCampo = '';
      }
    } else {
      this.Ordem = '';
    }

    switch (nomeCampo) {
      case 'Descricao':
        this.NomeCampo = 'Descricao';
        if (this.Ordem == 'asc') {
          this.Despesas.sort((a, b) =>
            a.Descricao.localeCompare(b.Descricao, 'pt-BR')
          );
        } else {
          this.Ordem = 'desc';
          this.Despesas.sort((a, b) =>
            b.Descricao.localeCompare(a.Descricao, 'pt-BR')
          );
        }
        break;
      case 'Categoria':
        this.NomeCampo = 'Categoria';
        if (this.Ordem == 'asc') {
          this.Despesas.sort((a, b) =>
            a.Categoria.Descricao.localeCompare(b.Categoria.Descricao, 'pt-BR')
          );
        } else {
          this.Ordem = 'desc';
          this.Despesas.sort((a, b) =>
            b.Categoria.Descricao.localeCompare(a.Categoria.Descricao, 'pt-BR')
          );
        }
        break;
      case 'Valor':
        this.NomeCampo = 'Valor';
        if (this.Ordem == 'asc') {
          this.Despesas.sort((a, b) => a.ValorInteiro - b.ValorInteiro && a.ValorCentavos - b.ValorCentavos);
        } else {
          this.Ordem = 'desc';
          this.Despesas.sort((a, b) => b.ValorInteiro - a.ValorInteiro && b.ValorCentavos - a.ValorCentavos);
        }
        break;
      case 'Data':
        this.NomeCampo = 'Data';
        if (this.Ordem == 'asc') {
          this.Despesas.sort((a, b) => new Date(a.DataDespesa).getTime() - new Date(b.DataDespesa).getTime());
        } else {
          this.Ordem = 'desc';
          this.Despesas.sort((a, b) => new Date(b.DataDespesa).getTime() - new Date(a.DataDespesa).getTime());
        }
        break;
      case 'Parcelas':
        this.NomeCampo = 'Parcelas';
        if (this.Ordem == 'asc') {
          this.Despesas.sort((a, b) => a.QtdParcelas - b.QtdParcelas);
        } else {
          this.Ordem = 'desc';
          this.Despesas.sort((a, b) => b.QtdParcelas - a.QtdParcelas);
        }
        break;
      default:
        this.NomeCampo = '';
        this.Despesas.sort((a, b) => b.Id - a.Id);
        break;
    }
    this.tratarIconeOrdenacao();
    this.DadosPaginador.PaginaAtual = 1;
    this.paginarDespesas();
  }

  tratarIconeOrdenacao() {
    let imgAsc = "<img src=\"../../../../assets/img/asc.png\" >";
    let imgDesc = "<img src=\"../../../../assets/img/desc.png\" >";
    let imgDescricao = document.getElementById('imgDescricao');
    let imgCategoria = document.getElementById('imgCategoria');
    let imgValor = document.getElementById('imgValor');
    let imgData = document.getElementById('imgData');
    let imgParcelas = document.getElementById('imgParcelas');

    imgDescricao!.innerHTML = '';
    imgCategoria!.innerHTML = '';
    imgValor!.innerHTML = '';
    imgData!.innerHTML = '';
    imgParcelas!.innerHTML = '';

    switch (this.NomeCampo) {
      case 'Descricao':
        if (this.Ordem == 'asc') {
          imgDescricao!.innerHTML = imgAsc;
        } else {
          imgDescricao!.innerHTML = imgDesc;
        }
        break;
      case 'Categoria':
        if (this.Ordem == 'asc') {
          imgCategoria!.innerHTML = imgAsc;
        } else {
          imgCategoria!.innerHTML = imgDesc;
        }
        break;
      case 'Valor':
        if (this.Ordem == 'asc') {
          imgValor!.innerHTML = imgAsc;
        }
        else {
          imgValor!.innerHTML = imgDesc;
        }
        break;
      case 'Data':
        if (this.Ordem == 'asc') {
          imgData!.innerHTML = imgAsc;
        } else {
          imgData!.innerHTML = imgDesc;
        }
        break;
      case 'Parcelas':
        if (this.Ordem == 'asc') {
          imgParcelas!.innerHTML = imgAsc;
        } else {
          imgParcelas!.innerHTML = imgDesc;
        }
        break;
      default:
        break;
      }
  }

  receberDadosPaginador($event: DadosPaginador): void {
    this.DadosPaginador = $event;
    this.paginarDespesas();
  }

  paginarDespesas() {
    this.DadosPaginados.Pagina = this.DadosPaginador.PaginaAtual;
    this.DadosPaginados.ItensPorPagina = this.DadosPaginador.ItensPorPagina;
    this.DadosPaginados.TotalItens = this.DadosPaginador.TotalItens;

    let itensPagina: ItemPagina<Despesa>[] = [];
    let pagina = 1;
    this.Despesas.forEach(item => {
      itensPagina.push({  Pagina: pagina, Item: item });
      if (itensPagina.length == this.DadosPaginados.ItensPorPagina * pagina) {
        pagina++;
      }
    });

    this.DadosPaginados.Itens = itensPagina.filter(item => item.Pagina == this.DadosPaginados.Pagina).map(item => item.Item);
  }

  exibirModalExcluir(despesa: Despesa): void {
    this.DespesaAcao = despesa;
    document.getElementById('modalExcluir')!.style.display = 'block';
  }
}
<div class="container main">
  <div class="titulo-pagina ff-verdana">
    <h1>Despesas</h1>
  </div>
  <div class="menu-painel conteudo-pagina">
    <div class="row">
      <div class="col col-flexible">
        <div class="input-group">
          <input type="text" class="form-control" placeholder="Buscar" name="Busca" id="txtBusca" (keydown.enter)="buscarDespesas()" (keydown.return)="buscarDespesas()">
          <span>
            <button title="Buscar" id="btnBuscar" class="btn" (click)="buscarDespesas()">
              <img src="../../../../assets/img/lupa-icon.png">
            </button>
          </span>
        </div>
      </div>
      <div class="col col-flexible">
        <button title="Nova Despesa" id="btnNovaDespesa" class="btn" routerLink="incluir-despesa">
          <img src="../../../../assets/img/plus-icon.png">
        </button>
      </div>
    </div>
  </div>
  <div class="conteudo-pagina div-tabela">
    <table class="table ff-verdana">
      <tr>
        <th (click)="ordenarDespesas('Acoes')" id="thAcoes" class="campo-ordenacao">
          <div class="titulo-tabela">
            <span class="nome-campo">Ações</span>
            <span class="img-ordenacao" id="imgAcoes"></span>
          </div>
        </th>
        <th (click)="ordenarDespesas('Descricao')" id="thDescricao" class="campo-ordenacao">
          <div class="titulo-tabela">
            <span class="nome-campo">Descrição</span>
            <span class="img-ordenacao" id="imgDescricao"></span>
          </div>
        </th>
        <th (click)="ordenarDespesas('Categoria')" id="thCategoria" class="campo-ordenacao">
          <div class="titulo-tabela">
            <span class="nome-campo">Categoria</span>
            <span class="img-ordenacao" id="imgCategoria"></span>
          </div>
        </th>
        <th (click)="ordenarDespesas('Valor')" id="thValor" class="campo-ordenacao">
          <div class="titulo-tabela">
            <span class="nome-campo">Valor</span>
            <span class="img-ordenacao" id="imgValor"></span>
          </div>
        </th>
        <th (click)="ordenarDespesas('Data')" id="thData" class="campo-ordenacao">
          <div class="titulo-tabela">
            <span class="nome-campo">Data</span>
            <span class="img-ordenacao" id="imgData"></span>
          </div>
        </th>
        <th (click)="ordenarDespesas('Parcelas')" id="thParcelas" class="campo-ordenacao">
          <div class="titulo-tabela">
            <span class="nome-campo">Qtd. Parcelas</span>
            <span class="img-ordenacao" id="imgParcelas"></span>
          </div>
        </th>
      </tr>
      <tr *ngFor="let Despesa of DadosPaginados.Itens">
        <td class="center">
          <div class="acoes">
            <span class="acoes-item">
              <button title="Editar" id="btnEditar{{Despesa.Id}}" class="btn btn-sm" routerLink="editar-despesa/{{Despesa.Id}}">
                <img src="../../../../assets/img/pencil-icon.png">
              </button>
            </span>
            <span class="acoes-item">
              <button title="Excluir" id="btnExcluir{{Despesa.Id}}" class="btn btn-sm" (click)="exibirModalExcluir(Despesa)">
                <img src="../../../../assets/img/lixeira-icon.png">
              </button>
            </span>
          </div>
        </td>
        <td>{{Despesa.Descricao}}</td>
        <td>{{Despesa.Categoria!.Descricao}}</td>
        <td class="right">{{Despesa.ValorInteiro!.toFixed()}},{{Despesa.ValorCentavos!.toFixed()}}</td>
        <td class="center">{{Despesa.DataDespesa  | date : 'shortDate'}}</td>
        <td class="center">{{Despesa.QtdParcelas}}</td>
    </table>
  </div>
  <div class="paginador">
    <app-paginador [DadosPaginador]="DadosPaginador" (messageEvent)="receberDadosPaginador($event)"></app-paginador>
  </div>
</div>
<app-excluir-despesa [Despesa]="DespesaAcao"></app-excluir-despesa>
